sys_script_include.script:
var SGentityMap = Class.create();
SGentityMap.prototype = {
    initialize: function() {
        this.entity_type_map = {};
        this.map_nids_type = {};
        var grNids = new GlideRecord("cmdb_ci_nids");
        grNids.addQuery("validated", true);
        grNids.query();
        while (grNids.next()) {
            var name = grNids.name.toString();
            this.map_nids_type[name] = grNids.network_type.getDisplayValue();
        }
        var grOT = new GlideRecord("cmdb_ot_entity_type");
        grOT.query();
        while (grOT.next()) {
            var label = grOT.label.toString();
            this.entity_type_map[label] = grOT.sys_id.toString();
        }

    },
    get_nids_type: function(name) {
        if (this.map_nids_type[name])
            return this.map_nids_type[name];
        else
            return "";
    },
    gettypes: function(type_ot) {
        if (this.entity_type_map[type_ot])
            return this.entity_type_map[type_ot];
        else
            return this.entity_type_map["Operational Technology Device"];
    },
    type: 'SGentityMap'
};
